import unittest

from FactorialRecursivo import factorial


class TestFactorial(unittest.TestCase):

    def test_factorial_5(self):
        resultado = factorial(5)
        self.assertEqual(resultado,120)
    
    def test_factorial_11(self):
        resultado = factorial(11)
        self.assertEqual(resultado,39916800)

    
    def test_factorial_27(self):
        resultado = factorial(27)
        self.assertEqual(resultado,10888869450418352160768000000)
    
    def test_factorial_34(self):
        resultado = factorial(34)
        self.assertEqual(resultado,295232799039604140847618609643520000000)
    
    def test_factorial_46(self):
        resultado = factorial(46)
        self.assertEqual(resultado,5502622159812088949850305428800254892961651752960000000000)
    
    def test_factorial_178(self):
        resultado = factorial(178)
        self.assertEqual(resultado,)

    def test_factorial_476(self):
        resultado = factorial(476)
        self.assertEqual(resultado,)

    def test_factorial_654(self):
        resultado = factorial(654)
        self.assertEqual(resultado,)
    
    def test_factorial_853(self):
        resultado = factorial(853)
        self.assertEqual(resultado,)


unittest.main()